mapper.select:
select count(*) from tbl_coupon 
where cp_UserId = #{cpUserid} 
and cp_Status != 1 and cp_EndDate >  now()

select count(1) from (
	select
	1
	from
	tbl_coupon
	t,tbl_activity b,
	tbl_couponvariety c,
	tbl_company co
	WHERE
	t.pk_Activity=b.pk_Activity
	AND co.cpy_id = b.act_ChannelType
	AND t.pk_CouponVariety = c.pk_CouponVariety


		and t.cp_userid=0 and t.cp_EndDate>#{currentDate}



		and t.cp_userid=0 and #{currentDate}>t.cp_EndDate



		and t.cp_userid!=0 and t.cp_status=1



	    and t.cp_userid!=0 and t.cp_status!=1 and
		t.cp_EndDate>#{currentDate}



		  and t.cp_userid!=0 and t.cp_status!=1 and
		#{currentDate}>t.cp_EndDate


	and t.cp_UserId = (SELECT u.user_id from tbl_user u where u.user_account = #{userPhone})


		and t.pk_activity= #{pkActivity}


		and t.pk_CouponVariety= #{pkCouponvariety}


		and t.cp_Limitation=#{cpLimitation}


		and b.act_Type=#{cpActivitytype}


		and t.cp_CouponCode=#{cpCouponcode}


		and c.cova_Scope= #{covaScope}


		and c.city_code= #{cityCode}


		and b.act_ChannelType= #{cpyId}

	) aa

select t.pk_Coupon, t.pk_Activity, (select act_ActivityName from tbl_activity where pk_Activity=t.pk_Activity ) actActivityname, t.pk_CouponVariety, (select cova_ActivityName from tbl_couponvariety where pk_CouponVariety=t.pk_CouponVariety ) covaActivityname, t.cp_Status, t.cp_Limitation, t.cp_CouponValue, t.cp_CouponCondition, t.cp_CouponCode, t.cp_UserId, c.city_code, c.cova_Scope, co.cpy_name cpyName, (select user_account from tbl_user usr where usr.user_id=t.cp_UserId) userPhone, date_format(t.cp_BeginDate,'%Y-%m-%d %T') cpBeginDate, date_format(t.cp_EndDate,'%Y-%m-%d %T') cpEndDate, date_format(t.cp_Createdate,'%Y-%m-%d %T') cpCreatedate, date_format(t.cp_Updatedate,'%Y-%m-%d %T') cpUpdatedate from tbl_coupon t,tbl_activity b, tbl_couponvariety c, tbl_company co WHERE t.pk_Activity=b.pk_Activity AND co.cpy_id = b.act_ChannelType AND t.pk_CouponVariety = c.pk_CouponVariety
and t.cp_userid=0 and t.cp_EndDate>#{currentDate}
and t.cp_userid=0 and #{currentDate}>t.cp_EndDate
and t.cp_userid!=0 and t.cp_status=1
and t.cp_userid!=0 and t.cp_status!=1 and t.cp_EndDate>#{currentDate}
and t.cp_userid!=0 and t.cp_status!=1 and #{currentDate}>t.cp_EndDate
and t.cp_UserId = (SELECT u.user_id from tbl_user u where u.user_account = #{userPhone})
and t.pk_activity= #{pkActivity}
and t.pk_CouponVariety= #{pkCouponvariety}
and t.cp_Limitation=#{cpLimitation}
and b.act_Type=#{cpActivitytype}
and t.cp_CouponCode=#{cpCouponcode}
and t.cp_CouponCode=#{cpCouponcode}
and c.cova_Scope= #{covaScope}
and c.city_code= #{cityCode}
and b.act_ChannelType= #{cpyId}
order by t.cp_Createdate desc limit #{pager.offset},#{pager.pageSize}

select t.pk_Coupon, t.pk_Activity, (select act_ActivityName from tbl_activity where pk_Activity=t.pk_Activity ) actActivityname, t.pk_CouponVariety, (select cova_ActivityName from tbl_couponvariety where pk_CouponVariety=t.pk_CouponVariety ) covaActivityname, t.cp_Status, t.cp_Limitation, t.cp_CouponValue, t.cp_CouponCondition, t.cp_CouponCode, t.cp_UserId, c.city_code, c.cova_Scope, co.cpy_name cpyName, (select user_account from tbl_user usr where usr.user_id=t.cp_UserId) userPhone, date_format(t.cp_BeginDate,'%Y-%m-%d %T') cpBeginDate, date_format(t.cp_EndDate,'%Y-%m-%d %T') cpEndDate, date_format(t.cp_Createdate,'%Y-%m-%d %T') cpCreatedate, date_format(t.cp_Updatedate,'%Y-%m-%d %T') cpUpdatedate from tbl_coupon t,tbl_activity b, tbl_couponvariety c, tbl_company co WHERE t.pk_Activity=b.pk_Activity AND co.cpy_id = b.act_ChannelType AND t.pk_CouponVariety = c.pk_CouponVariety
and t.cp_userid=0 and t.cp_EndDate>#{currentDate}
and t.cp_userid=0 and #{currentDate}>t.cp_EndDate
and t.cp_userid!=0 and t.cp_status=1
and t.cp_userid!=0 and t.cp_status!=1 and t.cp_EndDate>#{currentDate}
and t.cp_userid!=0 and t.cp_status!=1 and #{currentDate}>t.cp_EndDate
and t.cp_UserId = (SELECT u.user_id from tbl_user u where u.user_account = #{userPhone})
and t.pk_activity= #{pkActivity}
and t.pk_CouponVariety= #{pkCouponvariety}
and t.cp_Limitation=#{cpLimitation}
and b.act_Type=#{cpActivitytype}
and t.cp_CouponCode=#{cpCouponcode}
and t.cp_CouponCode=#{cpCouponcode}
and c.cova_Scope= #{covaScope}
and c.city_code= #{cityCode}
and b.act_ChannelType= #{cpyId}
order by t.cp_Createdate desc

mapper.update:
update tbl_coupon
set
cp_UserId = #{cpUserid}, cp_Updatedate = #{cpUpdatedate}
where
cp_CouponCode=#{cpCouponCode}

