mapper.select:
SELECT COUNT(1)
FROM fin_account_split_details b
INNER JOIN tbl_chargingorder co ON b.charging_order_id = co.pk_ChargingOrder
INNER JOIN tbl_company cpy ON cpy.cpy_id = b.cpy_id
where 1=1

	and b.pk_id = #{pkId}


	and b.cpy_id = #{cpyId}


	and b.account_id = #{accountId}


	and b.charging_order_id = #{chargingOrderId}


	and co.chOr_Code LIKE CONCAT('%',#{chargingOrderCode},'%')


	and co.chOr_ChargingStatus = #{chargingOrderStatus}


	and date(co.end_charge_time) >= #{startDate}


	and date(co.end_charge_time) <= #{endDate}

SELECT b.pk_id, b.cpy_id, b.account_id, b.split_mode, b.service_charge_split_amt, b.electricity_charge_split_amt, b.electric_power_split_amt, b.coupon_split_amt, b.charging_order_id, b.creator, b.modifier, b.gmt_create, b.gmt_modified, co.chOr_Code, co.chOr_QuantityElectricity, co.chOr_Moeny chOr_totalMoney, co.chOr_ChargeMoney, co.chOr_ServiceMoney, co.chOr_CouponMoney, co.chOr_ChargingStatus, co.end_charge_time, cpy.cpy_name FROM fin_account_split_details b INNER JOIN tbl_chargingorder co ON b.charging_order_id = co.pk_ChargingOrder INNER JOIN tbl_company cpy ON cpy.cpy_id = b.cpy_id where 1=1
and b.pk_id = #{pkId}
and b.cpy_id = #{cpyId}
and b.account_id = #{accountId}
and b.charging_order_id = #{chargingOrderId}
and co.chOr_Code LIKE CONCAT('%',#{chargingOrderCode},'%')
and co.chOr_ChargingStatus = #{chargingOrderStatus}
and date(co.end_charge_time) >= #{startDate}
and date(co.end_charge_time) <= #{endDate}
order by b.charging_order_id DESC, b.pk_id DESC limit #{pager.offset},#{pager.pageSize}
