mapper.select:
SELECT SUM(IF(p.puHi_ChargeType = 1,p.puHi_Monetary,0)) as AliPay, SUM(IF(p.puHi_ChargeType = 1,1,0)) as AliPayNum, SUM(IF(p.puHi_ChargeType = 2,p.puHi_Monetary,0)) as WxPay, SUM(IF(p.puHi_ChargeType = 2,1,0)) as WxPayNum, DATE_FORMAT( p.puHi_Createdate, '%Y-%m-%d' ) AS time FROM tbl_user_normal n, tbl_purchasehistory p WHERE n.user_id = p.puHi_UserId
AND p.puHi_Createdate > #{beginTime}
AND p.puHi_Createdate < DATE_ADD(#{endTime},INTERVAL 1 DAY)
AND p.puHi_Type = 4 AND p.puHi_ChargeType in (1,2) GROUP BY DATE_FORMAT( p.puHi_Createdate, '%Y-%m-%d' ) order by p.puHi_Createdate DESC
limit #{pager.offset},#{pager.pageSize}
