SELECT pk_ChargingOrder, chOr_Code, chOr_AppointmenCode, ep_num AS cpn, (ep.elPi_ElectricPileCode) AS eleCode, (ui.norm_real_name) AS usName, (go.grou_GroupName) AS goName, chOr_Moeny, chOr_QuantityElectricity, chOr_TimeQuantum, (eh.ePHe_ElectricpileHeadName) AS eleheadName, chOr_ChargingStatus, chOr_TransType, chOr_OrderType, chOr_CouponMoney, (select poSt_Name from tbl_powerstation where pk_PowerStation= ep.elPi_RelevancePowerStation) as chargePointName, (select uc_ExternalCardNumber from tbl_usercard where pk_UserCard= co.pk_UserCard ) as exterCardNumber FROM tbl_ChargingOrder co INNER JOIN (SELECT a.user_account, b.* FROM tbl_user a, tbl_user_normal b WHERE a.user_id = b.user_id) AS ui ON ui.user_id = co.chOr_UserId INNER JOIN tbl_Group AS go ON go.pk_Group = co.chOr_Type INNER JOIN tbl_ElectricPileHead AS eh ON eh.pk_ElectricpileHead = co.chOr_Muzzle INNER JOIN tbl_ElectricPile AS ep ON ep.pk_ElectricPile = co.chOr_PileNumber
1=1
and exists (select 1 from tbl_usercard where pk_UserCard= co.pk_UserCard and uc_ExternalCardNumber like concat('%',#{exterCardNumber},'%'))
and ep.elPi_OwnProvinceCode = #{provinceCode}
and ep.elPi_OwnCityCode = #{cityCode}
and ep.elPi_OwnCountyCode = #{countryCode}
and chOr_Code=#{chorCode}
and chOr_ChargingStatus=#{chorChargingstatus}
and ui.norm_real_name like concat('%',#{usName},'%')
and ui.user_account like concat('%',#{usPhone},'%')

SELECT pk_ChargingOrder, chOr_Code, chOr_AppointmenCode, ep_num AS cpn, (ep.elPi_ElectricPileCode) AS eleCode,
ep.owner_ship, ep.elPi_RateInformationId,
ui.user_account as userPhone, (ui.norm_real_name) AS usName,
chOr_Moeny, chOr_QuantityElectricity, chOr_TimeQuantum, pu.puhi_InvoiceStatus,
chOr_ChargingStatus, chOr_ChargeMoney chorChargemoney, chOr_ServiceMoney chorServicemoney, chOr_TransType, chOr_OrderType, begin_charge_time, end_charge_time, cr.chRe_QuantumDate, cr.chRe_JPrice, cr.chRe_FPrice, cr.chRe_PPrice, cr.chRe_GPrice, cr.chRe_ServiceCharge, chOr_CouponMoney, cp.cp_CouponCondition, cp.cp_Limitation, (select poSt_Name from tbl_powerstation where pk_PowerStation= ep.elPi_RelevancePowerStation) as chargePointName, (select uc_ExternalCardNumber from tbl_usercard where pk_UserCard= co.pk_UserCard ) as exterCardNumber FROM tbl_ChargingOrder co INNER JOIN tbl_user_normal_view ui ON ui.user_id = co.chOr_UserId AND ui.user_leval = '6' LEFT JOIN tbl_ElectricPile AS ep ON ep.elPi_ElectricPileCode = co.chOr_PileNumber LEFT JOIN tbl_chargingrecord AS cr ON cr.chRe_Code = co.chOr_Code LEFT JOIN tbl_purchasehistory AS pu ON pu.puHi_TransactionNumber = co.chOr_TransactionNumber AND pu.puHi_Type IN (1, 2) LEFT JOIN tbl_coupon AS cp ON cp.pk_Coupon = co.pk_Coupon 1=1
and exists (select 1 from tbl_usercard where pk_UserCard= co.pk_UserCard and uc_ExternalCardNumber like concat('%',#{exterCardNumber},'%'))
and ep.elPi_OwnProvinceCode = #{provinceCode}
and ep.elPi_OwnCityCode = #{cityCode}
and ep.elPi_OwnCountyCode = #{countryCode}
and ep.elPi_UserName = #{chorUser}
and ep.elPi_ElectricPileCode like concat('%',#{eleCode},'%')
and chOr_Code like concat('%',#{chorCode},'%')
and chOr_ChargingStatus=#{chorChargingstatus}
and (ui.norm_real_name like concat('%',#{usName},'%') or ui.user_account like concat('%',#{usName},'%'))
and ui.user_account like concat('%',#{usPhone},'%')
and begin_charge_time>=#{beginChargetime}
and end_charge_time<= date_add(#{endChargetime}, interval 1 day)
and pu.puhi_InvoiceStatus= #{puhiInvoiceStatus}
order by pk_ChargingOrder DESC limit #{pager.offset},#{pager.numPerPage}

select
count(pk_ChargingOrder)	
FROM tbl_ChargingOrder co inner JOIN (select a.user_account,b.user_id,b.norm_real_name from tbl_user a,tbl_user_normal b where a.user_leval='6' and a.user_id=b.user_id) AS ui ON ui.user_id = co.chOr_UserId
			  left JOIN tbl_ElectricPile AS ep ON ep.elPi_ElectricPileCode = co.chOr_PileNumber	
			  left JOIN tbl_chargingrecord AS cr ON cr.chRe_Code=co.chOr_Code
			  left JOIN tbl_purchasehistory AS pu ON co.chOr_TransactionNumber = pu.puHi_TransactionNumber AND pu.puHi_Type in (1,2)
			  LEFT JOIN (SELECT * from tbl_ElectricPileHead GROUP BY pk_ElectricPile) AS eh ON eh.pk_ElectricPile = ep.pk_ElectricPile 
			  and eh.ePHe_ElectricpileHeadId = co.chOr_Muzzle	
 1=1

	and	 exists (select 1 from tbl_usercard  where pk_UserCard= co.pk_UserCard and uc_ExternalCardNumber  like concat('%',#{exterCardNumber},'%'))


	and	ep.elPi_OwnProvinceCode = #{provinceCode}


	and	ep.elPi_OwnCityCode = #{cityCode}


	and	ep.elPi_OwnCountyCode = #{countryCode}



	and	ep.elPi_UserName = #{chorUser}


		and ep.elPi_ElectricPileCode like concat('%',#{eleCode},'%')


		and chOr_Code like concat('%',#{chorCode},'%')  


		and chOr_ChargingStatus=#{chorChargingstatus}


		and (ui.norm_real_name like concat('%',#{usName},'%') or ui.user_account like concat('%',#{usName},'%'))


		and ep.elPi_UserName= #{chorUser}


		and ui.user_account like concat('%',#{usPhone},'%')


	   and pu.puhi_InvoiceStatus= #{puhiInvoiceStatus}


		and begin_charge_time>=#{beginChargetime}


		and end_charge_time<=  date_add(#{endChargetime}, interval 1 day)

SELECT pk_ChargingOrder, chOr_Code, chOr_AppointmenCode, ep_num AS cpn, (ep.elPi_ElectricPileCode) AS eleCode, (ui.norm_real_name) AS usName, (ep.elPi_UserName) as comName, (ui.user_account) AS usPhone, chOr_Moeny, chOr_QuantityElectricity, chOr_TimeQuantum, (eh.ePHe_ElectricpileHeadName) AS eleheadName, chOr_ChargingStatus, chOr_TransType FROM tbl_ChargingOrder co INNER JOIN (SELECT a.user_account, b.* FROM tbl_user a, tbl_user_normal b WHERE a.user_id = b.user_id) AS ui ON ui.user_id = co.chOr_UserId INNER JOIN tbl_ElectricPileHead AS eh ON eh.pk_ElectricpileHead = co.chOr_Muzzle INNER JOIN tbl_ElectricPile AS ep ON ep.pk_ElectricPile = co.chOr_PileNumber
chOr_Type = 2 and chOr_ChargingStatus!=4
and chOr_Code=#{chorCode}
and chOr_ChargingStatus=#{chorChargingstatus}
and ui.norm_real_name like concat('%',#{usName},'%')
and ui.user_account=#{usPhone}

SELECT pk_ChargingOrder pkChargingorder, chOr_Code chorCode, chOr_AppointmenCode chorAppointmencode, ep_num AS cpn, (ep.elPi_ElectricPileCode) AS eleCode, ep.elPi_ElectricPileName chargePointName, ep.elPi_ElectricPileAddress chargePointAddress, ui.user_account userPhone, (a1.norm_real_name) AS usName, (ui.norm_real_name) AS comName, (ui.user_account) AS usPhone, chOr_Moeny chorMoeny, chOr_QuantityElectricity chorQuantityelectricity, chOr_TimeQuantum chorTimequantum, (eh.ePHe_ElectricpileHeadName) AS eleheadName, chOr_ChargingStatus chorChargingstatus, chOr_ChargeMoney chorChargemoney, chOr_ServiceMoney chorServicemoney, chOr_TransType chorTranstype, chOr_OrderType chorOrdertype FROM tbl_ChargingOrder co left JOIN tbl_ElectricPile AS ep ON ep.elPi_ElectricPileCode = co.chOr_PileNumber inner join (select a.user_account, a.user_id,a.norm_real_name FROM tbl_user_normal_view a WHERE a.user_leval = '5') AS ui on ui.user_id = ep.elPi_UserId left JOIN tbl_ElectricPileHead AS eh ON eh.pk_ElectricPile = ep.pk_ElectricPile and eh.ePHe_ElectricpileHeadId = co.chOr_Muzzle left JOIN tbl_user_normal_view AS a1 ON a1.user_id = co.chOr_UserId
1=1 and chOr_ChargingStatus!=4
and ep.elPi_UserId = #{loginUserId}
and chOr_Code like concat('%',#{chorCode},'%')
and ep.elPi_ElectricPileCode like concat('%',#{eleCode},'%')
and chOr_ChargingStatus=#{chorChargingstatus}
and (ui.norm_real_name like concat('%',#{usName},'%') or ui.user_account like concat('%',#{usName},'%') )
and ep.elPi_ElectricPileName like concat('%',#{chargePointName},'%')
and ep.elPi_ElectricPileAddress like concat('%',#{chargePointAddress},'%')
and begin_charge_time>=#{beginChargetime}
and end_charge_time<=date_add(#{endChargetime}, interval 1 day)
order by chOr_Createdate desc limit #{pager.offset},#{pager.numPerPage}

SELECT pk_ChargingOrder, chOr_Code, chOr_AppointmenCode, ep_num AS cpn, (ep.elPi_ElectricPileCode) AS eleCode, (ep.elPi_UserName) AS comName, chOr_Moeny, chOr_QuantityElectricity, chOr_TimeQuantum, (eh.ePHe_ElectricpileHeadName) AS eleheadName, chOr_ChargingStatus, chOr_TransType, go.grou_GroupName, chOr_OrderType, start_soc, end_soc FROM tbl_ChargingOrder co left JOIN tbl_PureBusiness AS pb ON pb.pk_PureBusiness = co.chOr_UserId left JOIN tbl_ElectricPileHead AS eh ON eh.pk_ElectricpileHead = co.chOr_Muzzle left JOIN tbl_ElectricPile AS ep ON ep.pk_ElectricPile = co.chOr_PileNumber left JOIN tbl_Group AS go ON go.pk_Group = co.chOr_Type
chOr_Type = 3 and chOr_ChargingStatus!=4
and chOr_Code=#{chorCode}
and chOr_ChargingStatus=#{chorChargingstatus}
and ep.elPi_UserName like concat('%',#{comName},'%')
and ep.elPi_ElectricPileCode=#{eleCode}

SELECT pk_ChargingOrder pkChargingorder, chOr_Code chorCode, chOr_AppointmenCode chorAppointmencode, ep.elPi_ElectricPileCode AS eleCode, ep.elPi_ElectricPileName chargePointName, chOr_Moeny chorMoeny, chOr_QuantityElectricity chorQuantityelectricity, chOr_TimeQuantum chorTimequantum, chOr_ChargingStatus chorChargingstatus, chOr_ChargeMoney chorChargemoney, chOr_ServiceMoney chorServicemoney, chOr_TransType chorTranstype, chOr_OrderType chorOrdertype, chor_parter_user_logo chorParterUserLogo, c.cpy_CompanyName partnerName FROM tbl_ChargingOrder co left JOIN tbl_ElectricPile AS ep ON ep.elPi_ElectricPileCode = co.chOr_PileNumber left JOIN tbl_ElectricPileHead AS eh ON eh.pk_ElectricPile = ep.pk_ElectricPile and eh.ePHe_ElectricpileHeadId = co.chOr_Muzzle LEFT JOIN tbl_user_business u on u.user_id = co.chOr_UserId left JOIN tbl_company c on c.cpy_CompanyNumber = co.chOr_OrgNo AND u.busi_company_id = c.pk_CompanyId
1=1 and chOr_ChargingStatus!=4 AND co.chOr_OrgNo !=0 AND co.chOr_OrgNo !=1001 AND co.chOr_OrgNo != 9999 and LENGTH(chor_parter_user_logo)!=0
AND c.cpy_CompanyName like CONCAT('%',#{partnerName},'%')
AND chor_parter_user_logo like CONCAT('%',#{chorParterUserLogo},'%')
and chOr_Code like concat('%',#{chorCode},'%')
and chOr_ChargingStatus=#{chorChargingstatus}
and ep.elPi_ElectricPileCode like concat('%',#{eleCode},'%')
and begin_charge_time>=#{beginChargetime}
and end_charge_time<=date_add(#{endChargetime}, interval 1 day)
group by pk_ChargingOrder order by chOr_Createdate desc limit #{pager.offset},#{pager.numPerPage}
